mapper.select:
select id, bpmn_name, bpmn_type, deployment_id, act_process_id, act_process_key, deploy_time, info from bus_bpmn_info
and deploy_time >= #{beginTime}
and DATE_FORMAT(deploy_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')

select id, bpmn_name, bpmn_type, deployment_id, act_process_id, act_process_key,
       deploy_time, info
from bus_bpmn_info where bpmn_type = #{type}
